mapper.update:
UPDATE template_steps
SET type = #{type},
    name = #{name},
    options = #{options}
    updated_datetime = #{updatedDatetime}
WHERE template_id = #{templateId}
AND template_step_num = #{templateStepNum}

mapper.delete:
DELETE FROM template_steps
WHERE template_id = #{templateId}
AND template_step_num = #{templateStepNum}
